const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const formatMessage = require('format-message');
const msg = require('./translation');

const { locale } = require('core-js');
require('sweetalert');
//async await estea
const ml5 = require('ml5');

let the_locale = null;

class Scratch3PushNotifyAPI {
    constructor(runtime) {
        the_locale = this._setLocale();
        this.runtime = runtime;
        this.payload={
            apikey:'',
            title:'',
            content:'',
            url:''
        }
    }

    getInfo() {
        the_locale = this._setLocale();
        return {
            id: 'pushnotifyapi',
            color1: '#202020', //'#0C5986',
            color2: '#FFFFFF',
            name: 'Push Notify API',
            blockIconURI: 'data:image/png;base64,',
            blocks: [
                
                {
                    opcode: 'setapikey',
                    blockType: BlockType.COMMAND,
                    text: msg.setapikey[the_locale],
                    arguments: {
                        KEY: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        }
                    },
                },
                {
                    opcode: 'settitle',
                    blockType: BlockType.COMMAND,
                    text: msg.settitle[the_locale],
                    arguments: {
                        TITLE: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        },                        
                    },
                },
                {
                    opcode: 'seturl',
                    blockType: BlockType.COMMAND,
                    text: msg.seturl[the_locale],
                    arguments: {
                        URL: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        },                        
                    },
                },                
                {
                    opcode: 'sendmessage',
                    blockType: BlockType.COMMAND,
                    text: msg.sendmessage[the_locale],
                    arguments: {
                        CONTENT: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        }
                    },
                },
                
            ],
            menus: {
                /*sticker_list: {
                    acceptReporters: true,
                    items: ['😀', '😕','😁', '😅', '🤣', '😍', '🤫', '🤔', '🤥','😪','😵‍💫','🥳','😭','💓','💔','👋','👌','💩']
                },*/
            }    

        };
    }

    // command blocks
   
    setapikey(args){
        this.payload.apikey=args.KEY;
        console.log('api_key=',this.payload.apikey);
    }

    settitle(args){
        this.payload.title=args.TITLE;
        console.log('title=',this.payload.title);
    }

    seturl(args){
        this.payload.url=args.URL;
        console.log('url=',this.payload.url);
    }


    async sendmessage(args){
        this.payload.content=args.CONTENT;
        try {
            this.payload.message = args.MESSAGE;
            console.log('Sending message:', this.payload);
            
            const response = await fetch(
                `https://xdroid.net/api/message?k=${this.payload.apikey}&t=${this.payload.title}&c=${this.payload.content}&u=${this.payload.url}`,
                {
                    method: 'get',
                    /*headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        chat_id: this.payload.chat_id,
                        text: this.payload.sticker+this.payload.message
                    })*/
                }
            );

            const result = await response.json();
            console.log('API Response:', result);

            if (!result.ok) {
                console.error('Telegram API Error:', result.description);
                return;
            }

            // 成功發送後再清空
            this.payload.apikey='';
            this.payload.content = '';
            this.payload.title = '';
            this.payload.url = '';
            
        } catch (error) {
            console.error('Error sending message:', error);
        }
    }
    
    _setLocale() {
            let now_locale = '';
            switch (formatMessage.setup().locale) {
                case 'zh-tw':
                    now_locale = 'zh-tw';
                    break;
                default:
                    now_locale = 'en';
                    break;
            }
            return now_locale;
        }
    

}

module.exports = Scratch3PushNotifyAPI;
