// Boiler plate from the Scratch Team

const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const formatMessage = require('format-message');
const msg = require('./translation');

const { locale } = require('core-js');
require('sweetalert');
//async await estea
const ml5 = require('ml5');
//const jqueryMin = require("./jquery.min.js");
//require('babel-polyfill');
//參考文件https://blog.sean.taipei/2016/10/telegram-bot


let the_locale = null;

class Scratch3TelegramBot {
    constructor(runtime) {
        the_locale = this._setLocale();
        this.runtime = runtime;
        this.payload ={
            token:'',
            message:'',
            imageFile:'',
            chat_id : '',
        };
        this.exec = 'https://script.google.com/macros/s/AKfycbyPMsaLgEEcia39_vcK1AmSGfpAi2YViAUcZFMbmKdYJ5niqDVui9Rgb4241Zdwca5d/exec';
    }

    getInfo() {
        the_locale = this._setLocale();
        return {
            id: 'telegrambot',
            color1: '#336600', //'#0C5986',
            color2: '#FFFFFF',
            name: 'Telegram Bot',
            blockIconURI: 'data:image/png;base64,',
            blocks: [
                //https://unicode.org/emoji/charts/full-emoji-list.html
                {
                    opcode: 'checkOut',
                    blockType: BlockType.COMMAND,
                    text: msg.checkOut[the_locale],
                },
                {
                    opcode: 'checkchatid',
                    blockType: BlockType.COMMAND,
                    text: msg.checkchatid[the_locale],
                    arguments: {
                        TOKEN: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        }
                    },
                },
                {
                    opcode: 'gettoken',
                    blockType: BlockType.COMMAND,
                    text: msg.Token[the_locale],
                    arguments: {
                        TOKEN: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                            //defaultValue: 'ws://broker.emqx.io:8083/mqtt'
                        }
                    },
                },
                {
                    opcode: 'get_chat_id',
                    blockType: BlockType.COMMAND,
                    text: msg.chat_id[the_locale],
                    arguments: {
                        ID: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        },
                        
                    },
                },
                {
                    opcode: 'sendimagefile',
                    blockType: BlockType.COMMAND,
                    text: msg.SendImageFile[the_locale],
                    arguments: {
                        URL: {
                            type: ArgumentType.STRING,
                            defaultValue: 'https://.png',
                        }
                    },
                },
                {
                    opcode: 'sendsticker',
                    blockType: BlockType.COMMAND,
                    text: msg.Sendsticker[the_locale],
                    arguments: {
                        STICKER: {
                            type: ArgumentType.STRING,
                            defaultValue: '😀',
                            menu: 'sticker_list'
                        },
                    },
                },
                {
                    opcode: 'setsendtext',
                    blockType: BlockType.COMMAND,
                    text: msg.setsendtext[the_locale],
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',                            
                        },
                    },
                },
                {
                    opcode: 'sendmessage',
                    blockType: BlockType.COMMAND,
                    text: msg.SendMessage[the_locale],
                    /*arguments: {
                        MESSAGE: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        }
                    },*/
                },
                
            ],
            menus: {
                sticker_list: {
                    acceptReporters: true,
                    items: ['😀', '😕','😁', '😅', '🤣', '😍', '🤫', '🤔', '🤥','😪','😵‍💫','🥳','😭','💓','💔','👋','👌','💩']
                },
            }    

        };
    }

    // command blocks
    checkOut(){
        window.open('https://unicode.org/emoji/charts/full-emoji-list.html');
    }

    checkchatid(args){
        apikey=args.TOKEN;
        window.open('https://api.telegram.org/bot'+apikey+'/getUpdates');
    }

    get_chat_id(args){
        this.payload.chat_id=args.ID;
    }

    gettoken(args){
        this.payload.token = args.TOKEN;
        console.log(this.payload.token);
    }

    setsendtext (args) {
        this.payload.message = this.payload.message+args.TEXT;
        console.log('message=',this.payload.message);
    }

    async sendmessage(args){
        try {
            //this.payload.message = args.MESSAGE;
            console.log('Sending message:', this.payload);
            
            const response = await fetch(
                `https://api.telegram.org/bot${this.payload.token}/sendMessage`,
                {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        chat_id: this.payload.chat_id,
                        text: this.payload.message
                    })
                }
            );

            const result = await response.json();
            console.log('API Response:', result);

            if (!result.ok) {
                console.error('Telegram API Error:', result.description);
                return;
            }

            // 成功發送後再清空
            this.payload.message = '';
            this.payload.sticker = '';
            this.payload.imageFile = '';
            
        } catch (error) {
            console.error('Error sending message:', error);
        }
    }
    
    async sendimagefile(args) {
        try {
            this.payload.imageFile = args.URL;
            console.log('Sending image:', this.payload);
            
            const response = await fetch(
                `https://api.telegram.org/bot${this.payload.token}/sendPhoto`,
                {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        chat_id: this.payload.chat_id,
                        photo: this.payload.imageFile,  // 直接使用圖片URL
                        caption: '圖片來自Scratch'  // 可選的圖片說明
                    })
                }
            );

            const result = await response.json();
            console.log('API Response:', result);

            if (!result.ok) {
                console.error('Telegram API Error:', result.description);
                return;
            }

            // 成功發送後清空圖片URL
            this.payload.imageFile = '';
            
        } catch (error) {
            console.error('Error sending image:', error);
        }
    }

    sendsticker(args){
        this.payload.message = this.payload.message+args.STICKER;
    } 
    // end of block handlers

    _setLocale() {
        let now_locale = '';
        switch (formatMessage.setup().locale) {
            case 'zh-tw':
                now_locale = 'zh-tw';
                break;
            default:
                now_locale = 'en';
                break;
        }
        return now_locale;
    }

    // helpers

}

module.exports = Scratch3TelegramBot;
